import axios from "axios";
import "./MovieCard.css";
import { useDispatch } from "react-redux";
import { addToFavouriteFailure, addToFavouriteSuccess } from "../../store/favouriteSlice";

const MovieCard = ({
  movies,
  favouriteMovie: FavouriteMovie,
  f, 
  setF
}) => {
  const dispatch = useDispatch();
  const prepareData = (apiResponse) => {
      return {
          id: apiResponse.imdbID,
          poster: apiResponse.Poster,
          type: apiResponse.Type,
          title: apiResponse.Title,
          year : apiResponse.Year
      };

  }

  const convert = (item) => {
    return ({
      imdbID: item.id,
      Poster: item.poster,
      Type: item.type,
      Title: item.title,
      Year: item.year
    })
  }
  const addFavoriteMovie = async(movie) => {
      try {
          const movieData = prepareData(movie);
          const token = localStorage.getItem("token");

          const response = await axios.post(`${process.env.REACT_APP_BACKEND_BASE_URL}/favourites/`, movieData, {
              headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${token}`
              }
          });
          const data = convert(response.data.movie)
          dispatch(addToFavouriteSuccess(data));
          setF(f+1)
      } catch (error) {
          console.error('Error:', error);
          dispatch(addToFavouriteFailure(error.message));
      } 
  };

  return (
    <div className="movie-row-container">
      {movies && movies?.map(
        (movie) =>
          movie.Poster.match(/\.(jpeg|jpg|gif|png)$/) != null && (
            <div className="movie-frame" key={movie.imdbID}>
              <img className="movie-image" src={movie.Poster} alt="movie" />
              <p className="movie-header">{movie.Type}</p>
              <div
                className="overlay-container"
                onClick={() => addFavoriteMovie(movie)}
                >
                <FavouriteMovie />
              </div>
              <h1>{movie.title}</h1>
            </div>
          )
      )}
    </div>
  );
};

export default MovieCard;
