import axios from "axios";
import "./MovieCard.css";

const MovieCard = ({
  movies,
  handleFavorite,
  favouriteMovie: FavouriteMovie,
}) => {

  const prepareData = (apiResponse) => {
      return {
          id: apiResponse.imdbID,
          poster: apiResponse.Poster,
          type: apiResponse.Type,
          title: apiResponse.Title,
          year : apiResponse.Year
      };

  }


  const addFavoriteMovie = async(movie) => {

      try {
          const movieData = prepareData(movie);
          console.log("moviedata...", movieData);
          const token = localStorage.getItem("token");

          const response = await axios.post(`${process.env.REACT_APP_BACKEND_BASE_URL}/favourites/`, movieData, {
              headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${token}`
              }
          });
          console.log("add res...", response);
      } catch (error) {
          console.error('Error:', error);
      } 
  };

  return (
    <div className="movie-row-container">
      {movies?.map(
        (movie) =>
          movie.Poster.match(/\.(jpeg|jpg|gif|png)$/) != null && (
            <div className="movie-frame" key={movie.imdbID}>
              <img className="movie-image" src={movie.Poster} alt="movie" />
              <p className="movie-header">{movie.Type}</p>
              <div
                className="overlay-container"
                onClick={() => addFavoriteMovie(movie)}
                >
                <FavouriteMovie />
              </div>
              <h1>{movie.title}</h1>
            </div>
          )
      )}
    </div>
  );
};

export default MovieCard;
